def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

#user input
def get_user_input():
    user_input = entry_1.get()
    print(user_input)
 
#summary output
def fetch_summary():
    query = entry_1.get()
    try:
        summary = wikipedia.summary(query, sentences=5)
        text_box.delete("1.0", "end")  
        text_box.insert("1.0", summary)  
    except wikipedia.exceptions.PageError:
        text_box.delete("1.0", "end")
        text_box.insert("1.0", "No page found for this topic.")
    except wikipedia.exceptions.DisambiguationError as e:
        text_box.delete("1.0", "end")
        text_box.insert("1.0", f"Multiple results found. Try a more specific query.\n{e.options[:5]}") 


#Text to voice
engine = pyttsx3.init() 
rate = engine.getProperty('rate')   
print (rate)                        
engine.setProperty('rate', 125)     

def text_read():
    engine.say(text_box.get("1.0", "end"))
    engine.runAndWait()

